import styled from 'styled-components';
import { darken } from 'polished';
import versusLinha from '../../assets/imagens/div.png';

export const Comparação = styled.div`
    margin: 90px 0;
    padding: 0 120px;
    max-width: 100vw;
    box-sizing: border-box;

    display: flex;
    flex-direction: column;

    justify-content: space-between;

    @media(max-width: 909px){
        padding: 0 10px;
    }

    h2 {
        color: #222;
        text-align: center;
        font-size: 3rem;
        font-weight: 100;
        font-family: Bebas neue;
        letter-spacing: 3px;

        @media(max-width: 689px){
            font-size: 2rem;
        }
    }

    h3 {
        color: #222;
        text-align: center;
        font-size: 1.5rem;
        font-weight: 100;
        font-family: Bebas neue;
        letter-spacing: 3px;
        margin-bottom: 40px;

        @media(max-width: 689px){
            font-size: 2rem;
        }
    }

    a {
        display: flex;

        padding: 10px 30px;
        align-self: center;

        margin-top: 40px;

        border-radius: 4px;
        background-color: #62e987;

        &::after{
            content: "Ler mais+";
        }

        &:hover {
            background: ${darken(0.02, '#62e987')}
        }
    }
`;

export const Requisitos = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 50px;

    p {
        display: flex;
        flex-direction: column;
        text-transform: uppercase;

        justify-content: center;
        align-items: center;
        text-align: center;

        min-width: 150px;
        min-height: 100px;

        border-radius: 6px;


        margin-right: 70px;

        background: #62e987;

        color: #222;
        text-align: start;
        height: 60px;

        :nth-child(1) {
            &::after {
                margin-top: 10px;
                content: "Rapidez";
            }
        }

        :nth-child(2) {
            &::after {
                margin-top: 10px;
                content: "Latência";
            }
        }

        :nth-child(3) {
            &::after {
                margin-top: 10px;
                content: "Dispositivos por km²";
                text-align: center;
                width: 50px;
            }
        }

        :nth-child(4) {
            &::after {
                margin-top: 10px;
                content: "Frequência";
            }
        }


    }

    @media(max-width:689px){
        p {
            font-size: 0.9rem;
            padding-left: 10px;
        }
    }


`;
