import styled from 'styled-components';

export const Container = styled.div`
    table {

        tr, th, td {
            padding: 5px 20px;
            font-size: 1rem;
        }

        tr {
            &:nth-child(1) {
                text-align: center;
                font-family: Bebas neue;
            }

            td {
                &:nth-child(3n - 1) {
                    text-align: center;
                }
                &:nth-child(4n - 1) {
                    text-align: center;
                }
            }
        }
    }
`;
