import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 100px 0;
    font-size: 1.2rem;

    h2 {
        text-align: center;
        font-size: 2rem;
        font-weight: 100;
        font-family: Bebas neue;
        letter-spacing: 3px;
        margin-bottom: 40px;
    }

    table {

        svg {
            margin-right: 7px;
            color: #20B730;
        }

        tr {

            &:nth-child(1) {
                font-family: Bebas neue;
                font-size: 2rem;
                background: #4cc789;
            }

            td {

                padding: 10px 25px;

                &:nth-child(4){
                    background: #4cc789;
                }

                &:nth-child(1) {
                    text-align: start;
                    padding-left: 10px;
                    padding-right: 10px;
                    color: #000;

                }

                &:nth-child(2) {
                    text-align: center;
                    background: #ed5a55;
                }

                &:nth-child(3) {
                    text-align: center;
                    background: #97FF83;
                }

            }
        }
    }
`;
