import styled from 'styled-components';

export const Header = styled.header`
    position: fixed;
    display: flex;
    width: 100%;
    padding: 10px 110px;
    background: #000;
    color: #fff;
    justify-content: space-between;
    align-items: center;
    z-index: 3;

    div {
        svg {
            &:nth-child(1){
                z-index: 3;
            }
        }
            &:nth-child(2){
                position: absolute;
                right: 7.2%;
                bottom: 50%;
                z-index: 3;
            }
        }
    }

    @media (max-width: 750px){
        padding: 10px 50px;
    }

`;
