import styled from 'styled-components';
import { darken } from 'polished';
import versusLinha from '../../assets/imagens/div.png';

export const Comparação = styled.div`
    margin: 90px 0;
    padding: 0 120px;
    max-width: 100vw;
    box-sizing: border-box;

    display: flex;
    flex-direction: column;

    justify-content: space-between;

    @media(max-width: 909px){
        padding: 0 10px;
    }

    h2 {
        color: #222;
        text-align: center;
        font-size: 3rem;
        font-weight: 100;
        font-family: Bebas neue;
        letter-spacing: 3px;

        @media(max-width: 689px){
            font-size: 2rem;
        }
    }

    h3 {
        color: #222;
        text-align: center;
        font-size: 1.5rem;
        font-weight: 100;
        font-family: Bebas neue;
        letter-spacing: 3px;
        margin-bottom: 40px;

        @media(max-width: 689px){
            font-size: 2rem;
        }
    }

    a {
        display: flex;

        padding: 10px 30px;
        align-self: center;

        margin-top: 40px;

        border-radius: 4px;
        background-color: #62e987;

        &::after{
            content: "Ler mais+";
        }

        &:hover {
            background: ${darken(0.02, '#62e987')}
        }
    }

`;

export const Requisitos = styled.div`
    div {
        display: grid;
        grid-template-columns: 1fr 1fr 1fr 1fr;
        justify-content: center;
        align-items: center;

        &:nth-child(1){
            grid-column: 1;
            button {
                &::after {
                    content: "Velocidade";
                }
            }
        }

        &:nth-child(2){
            grid-column: 2;
            button {
                &::after {
                    content: "Latência";
                }
            }
        }

        &:nth-child(3){
            grid-column: 2;
            button {
                &::after {
                    content: "Dispositivos por km²";
                }
            }
        }

        &:nth-child(4){
            button {
                &::after {
                    content: "Frequência";
                }
            }
        }

        button {
            display: flex;
            flex-direction: column;
            border: none;
            text-transform: uppercase;
            background: #62e987;
            color: #222;
            font-size: 1.2rem;

            justify-content: center;
            align-items: center;
            text-align: center;

            padding: 30px;
            border-radius: 10px;
        }
    }
`;

export const Conteudo = styled.div`
`;
