import {createGlobalStyle} from 'styled-components';

export default createGlobalStyle `
    max-width: 700px;
    background: #fff;
    border-radius: 4px;
    box-sizing: 0 0 20px rgba(0, 0, 0, 0.1);
    padding: 30px;
    margin: 80px auto;

    h1{
        font-size: 20px;
        display: flex;
        flex-direction: row;
        align-items: center;

        svg{
            margin-right: 5px;
        }
    }
`;
