import styled from 'styled-components';

export const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;

    table {

        tr {

            &:nth-child(1) {
                font-family: Bebas neue;
                font-size: 2rem;
                background: #fff;
            }

            td {

                padding: 10px 25px;

                &:nth-child(1) {
                    text-align: start;
                    background: #ccc;
                    padding-left: 0px;
                }

                &:nth-child(2) {
                    text-align: center;
                    background: #ccc;
                }

                &:nth-child(3) {
                    text-align: center;
                    background: #97FF83;
                }

            }
        }
    }
`;
