import React, {useState} from 'react';
import { Comparação, Requisitos, Conteudo} from './styles';
import {FaTachometerAlt, FaStopwatch, FaMobileAlt, FaBroadcastTower} from 'react-icons/fa';
import { Link } from 'react-router-dom';

export default function ComparativoGrande(){
    const [active, setActive] = useState(true);
    const [comparativos] = useState([{vetor: FaBroadcastTower}], [{}], [{}])
    return (
        <Comparação>

            <h2>Comparação</h2>
            <h3>(4G e 5G)</h3>

            <Requisitos>

                <Vel active={active}>
                    <button type="button"><FaTachometerAlt size={35}/></button>
                    <Conteudo><p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Id perspiciatis mollitia voluptatem?</p></Conteudo>
                </Vel>

                <Lat >
                    <button type="button" ><FaStopwatch size={35}/></button>
                    <Conteudo><p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Id perspiciatis mollitia voluptatem?</p></Conteudo>
                </Lat>

                <Disp>
                    <button type="button"><FaMobileAlt size={35}/></button>
                    <Conteudo><p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Id perspiciatis mollitia voluptatem?</p></Conteudo>
                </Disp>

                <Freq>
                    <button type="button"><FaBroadcastTower size={35}/></button>
                    <Conteudo><p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Id perspiciatis mollitia voluptatem?</p></Conteudo>
                </Freq>
            </Requisitos>

            <Link to="/"></Link>
        </Comparação>

    );
}
