import styled from 'styled-components';
import hero from '../../assets/imagens/hero.png'
import versusLinha from '../../assets/imagens/div.png'

export const Hero = styled.div`
    display: flex;
    flex-direction: column;
    background-image: url(${hero});
    background-attachment: fixed;
    background-size: cover;
    background-position: center;
    width: 100vw;
    height: 100vh;
    justify-content: center;
    align-items: center;

    h1 {
        color: #000;
        font-size: 2.5rem;
        text-align: center;
        margin-bottom: 10px;
    }

    p {
        font-size: 1rem;
    }


`;

export const Pag2 = styled.div`
    display: flex;
    flex-direction: column;

    justify-items: center;
    justify-content: center;
    align-items: center;
    align-content: center;

    h2 {
        text-align: center;
        font-size: 3rem;
        font-family: Bebas neue;
        letter-spacing: 3px;
        z-index: 2;
        margin-bottom: 50px;
    }
`;

export const Meio = styled.div`
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    z-index: 2;
    align-items: center;
`;

export const Requisitos = styled.div`
    justify-self: start;
    h3 {
        font-size: 4rem;
        font-family: Bebas neue;
        margin-bottom: 10px;
    }

    p {
        padding: 20px 0px;
        font-size: 1.2rem;
        color: #20B730;
        text-align: start;
    }
`;

export const MeioMeio = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`;

export const QuatroG = styled.div`

`;

export const Versus = styled.div`
    display: grid;
    width: 70px;
    height: 300px;
    margin-bottom: 13px;
    align-content: center;
    margin-right: 20px;
    margin-left: 20px;
    align-items: center;
    justify-items: center;

    div {
        grid-row: 1;
        grid-column: 1;
        width: inherit;
        height: inherit;
        background-image: url(${versulinha});
        background-position: center;
        background-repeat: no-repeat;
        background-size: cover;
    }
`;

