import styled from 'styled-components';

export const Container = styled.div`
    table {

        tr {
            &:nth-child(1){

                padding-bottom: 500px;
                margin-bottom: 500px;

                td {

                    &:nth-child(1){
                        text-align: center;

                        font-family: Bebas neue;
                        font-size: 2.5rem;
                    }
                }
            }

            td {

                padding: 10px 25px;

                &:nth-child(3n - 1) {
                    text-align: center;
                }

                &:nth-child(4n - 1) {
                    text-align: center;
                }

                &:nth-child(4n, + 2) {
                    background: #4cc789;
                }

            }
        }
    }
`;
