import styled from 'styled-components';
const hero = '../assets/imagens/hero.png'

export const Hero = styled.div`
    display: flex;
    background: #4cc789 url(${hero});
    width: 100vw;
    height: 100vh;
    justify-content: center;
    align-items: center;

    h1{
        color: #000;
    }
`;
