import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import {FaFacebook, FaCaretUp} from 'react-icons/fa';
import { AiFillInstagram, AiFillTwitterCircle } from 'react-icons/ai';
import {Hero, Video, Conteudo, Footer, RedesSociais, Info} from './styles';

import ContainerPequeno from '../../components/Comparativo-pequeno';
import ContainerGrande from '../../components/Comparativo-grande';

export default function Main(){

    const [videos] = useState([
        {url: 'http://www.youtube.com/embed/tqeC-cCIUTk?autoplay=0', active: false},
        {url: 'http://www.youtube.com/embed/Qky8NVaAfK8?autoplay=0', active: false},
        {url: 'http://www.youtube.com/embed/ya_u4o5MwJ4?autoplay=0', active: false},
        {url: 'http://www.youtube.com/embed/al5RtYgoD_I?autoplay=0', active: false}
    ]);

    return(
        <>
            <Hero>
                <h1>Conheça o 5G Sustentável</h1>
                <p>"O futuro da telecomunicação móvel e da sustentabilidade"</p>
            </Hero>

            {window.innerWidth > 950 ? <ContainerGrande/> : <ContainerPequeno/>}



            <Video>
                <h2>Vídeos</h2>
                <ul>
                    {videos.map(video => (
                        <li key={video.url}> <iframe type="text/html" src={video.url} frameborder="0" allowfullscreen="allowfullscreen"/> </li>
                    ))}
                </ul>
            </Video>

            <Conteudo>
                <h3>A Tecnologia 5G e a sustentabilidade</h3>
                <hr></hr>
                <p>O 5G é a próxima geração de rede de dados sem fio e será capaz de fornecer 20 vezes mais velocidade de internet do que o 4G.  De fato, o 5G trará  novas formas de serviços, incluindo tudo, desde monitorar remotamente o uso da  agua nas fazendas até  realizar remotamente cirurgias cerebrais, Devido a sua baixíssima latência.</p>

                <p>A capacidade da 5G de conectar bilhões de sensores e dispositivos fornecerá novas idéias sobre os complexos problemas modernos que enfrentamos continuamente.</p>

                <p>O 5G representa uma oportunidade para repensar a organização da sociedade, a fim de apoiar abordagens sustentaveis para a vida.</p>
                <Link to="/"></Link>
            </Conteudo>

            <Footer>
                <Info>
                    <a>Fale Conosco</a>
                    <p>\</p>
                    <a>Sobre nós</a>
                    <p>\</p>
                    <a>Legal</a>
                </Info>

                <a href=""><FaCaretUp size={25}/></a>

                <RedesSociais>
                    <a href=""><FaFacebook size={25}/></a>
                    <a href="" ><AiFillInstagram size={27}/></a>
                    <a href=""><AiFillTwitterCircle size={27}/></a>
                </RedesSociais>
            </Footer>

        </>
    );
}
