import styled from 'styled-components';
import hero from '../../assets/imagens/hero.png'

export const Hero = styled.div`
    display: flex;
    flex-direction: column;
    background-image: url(${hero});
    background-attachment: fixed;
    background-size: cover;
    background-position: center;
    width: 100vw;
    height: 100vh;
    justify-content: center;
    align-items: center;

    h1 {
        color: #000;
        font-size: 2.5rem;
        text-align: center;
        margin-bottom: 10px;
    }

    p {
        font-size: 1rem;
    }


`;

export const Pag2 = styled.div`
    width: 100vw;
    height: calc(100vh - 60px);

`;

export const Versus = styled.table`
    border: 1px solid;
    font-size: 1.8rem;
    tr {
        th {
            border: 1px solid;
        }
        td {
            border: 1px solid;
        }
    }
`;
