import styled from 'styled-components';
import hero from '../../assets/imagens/hero.png'

export const Hero = styled.div`
    display: flex;
    flex-direction: column;
    background-image: url(${hero});
    background-attachment: fixed;
    background-size: cover;
    background-position: center;
    width: 100vw;
    height: 100vh;
    justify-content: center;
    align-items: center;

    h1 {
        color: #000;
        font-size: 2.5rem;
        text-align: center;
        margin-bottom: 10px;
    }

    p {
        font-size: 1rem;
    }


`;

export const Pag2 = styled.div`
    display: flex;
    flex-direction: column;

    justify-items: center;
    justify-content: center;
    align-items: center;
    align-content: center;

    h2 {
        text-align: center;
        font-size: 3rem;
        font-family: Bebas neue;
        letter-spacing: 3px;
        z-index: 2;
        margin-bottom: 50px;
    }
`;

export const Meio = styled.div`
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    z-index: 2;
    align-items: center;
`;

export const Requisitos = styled.div`
    justify-self: start;
    h3 {
        font-size: 4rem;
        font-family: Bebas neue;
        margin-bottom: 10px;
    }
`;

