import styled from 'styled-components';

export const Container = styled.div`
    table {
        border: 1px solid #000;

        tr, th, td {
            border: 1px solid #000;
        }

        tr {
            &:nth-child(1) {
                text-align: center;
            }

            td {
                &:nth-child(3) {
                    text-align: start;
                }
            }
        }
    }
`;
