import styled from 'styled-components';

export const Headers = styled.header`
    visibility: ${props => props.visibility ? 1 : 0} ;
    position: fixed;
    display: flex;
    width: 100%;
    padding: 10px 110px;
    background: ${props => props.visibility ? "" : "#000"};
    color: #fff;
    justify-content: space-between;
    align-items: center;
    z-index: 3;
    transition: .2s;

    div {
        display: grid;
        gap: 10px;
        svg {
            &:nth-child(1){
                grid-column: 1;
                grid-row: 1;
            }

            &:nth-child(2){
                margin-left: 83%;
                margin-top: 23%;
                grid-column: 1;
                grid-row: 1;
            }
        }
    }

    @media (max-width: 750px){
        padding: 10px 50px;
    }

`;
